import Nav from "./Nav.js";
import Title from "./Title.js";
import ShopComponent from "./ShopComponent.js";
import Carrot from './carrots.jpg';
import Beans from './green_beans.jpeg';
import MangeItem from './MangeItem.js';
import Cart from './Cart.js';
import { useEffect, useState } from "react";
import axios from "axios";


const cart = [{
    id: 1005,
    name: "Carrot",
    price: 650,
    bio: "Grown in Nuwara Eliya, Sri Lanka",
    imageName: Carrot,
    quantity: 3
},{
    id: 1007,
    name: "Beans",
    price: 758,
    bio: "Grown in Matale, Sri Lanka",
    imageName: Beans,
    quantity: 5
}]

function Shop() {

    const [itemList, setItemList] = useState([]);
    const [pageNum, setPageNum] = useState(1);
    const [isEdit, setIsEdit] = useState(false);
    const [isSubmit, setIsSubmit] = useState(false);
    const [selectItem, setSelectItem] = useState({});

    const addItem = (data) => {
        axios.post(`http://localhost:3001/additem`, data)
            .then(response => {
                getItem();
            })
            .catch(error => {
                console.log(error);
            });
    }; 

    const getItem = () => {
        axios.get('http://localhost:3001/items')
            .then(response => {
                setItemList(response.data);
            })
            .catch(error => {
                console.log(error);
            })
    };

    useEffect(() => {
        getItem();
    }, []);

    function pageSelect() {
        if (pageNum == 1) {
            return <ShopComponent itemList={itemList}/>
        }else if (pageNum == 2) {
            return <MangeItem selectItem={selectItem} setSelectItem={(data) => setSelectItem(data)} isSubmit={isSubmit} setIsSubmit={(data) => setIsSubmit(data)} getItem={getItem} itemList={itemList} setIsEdit={(data) => setIsEdit(data)} isEdit={isEdit} addItem={addItem}/>
        }else if (pageNum == 3) {
            return <Cart cart={cart} setPageNum={(data) => setPageNum(data)}/>
        }
    }

    function pageTitle() {
        if (pageNum == 1) {
            return "Shop"
        }else if (pageNum == 2) {
            return "Manage Item"
        }else if (pageNum == 3) {
            return "Cart"
        }
    }

    return(
        <div className="shop-container">
            <Nav setPageNum={(data) => setPageNum(data)} cart={cart}/>
            <Title pageTitle={pageTitle}/>
            {
                pageSelect()
            }
            
        </div>
    )
}

export default Shop;
