import Nav from "./Nav.js";
import Title from "./Title.js";
import ShopComponent from "./ShopComponent.js";
import Carrot from './carrots.jpg';
import Beans from './green_beans.jpeg';
import Carbbage from './carbbage.jpg';
import ladiesFingers from './ladies_finger.jpg';
import Pumpkin from './Pumpkin.webp';
import MangeItem from './MangeItem.js';
import Cart from './Cart.js';
import { useState } from "react";
import axios from "axios";



const cart = [{
    id: 1005,
    name: "Carrot",
    price: 650,
    bio: "Grown in Nuwara Eliya, Sri Lanka",
    imageName: Carrot,
    quantity: 3
},{
    id: 1007,
    name: "Beans",
    price: 758,
    bio: "Grown in Matale, Sri Lanka",
    imageName: Beans,
    quantity: 5
}]

function Shop() {

    const itemList = [{
        id: 1005,
        name: "Carrot",
        price: 650,
        bio: "Grown in Nuwara Eliya, Sri Lanka",
        imageName: Carrot
    },{
        id: 1007,
        name: "Beans",
        price: 758,
        bio: "Grown in Matale, Sri Lanka",
        imageName: Beans
    },{
        id: 1009,
        name: "Cabbage",
        price: 105,
        bio: "Grown in Nuwara Eliya, Sri Lanka",
        imageName: Carbbage
    },{
        id: 1004,
        name: "Pumpkin",
        price: 400,
        bio: "Grown in Yala, Sri Lanka",
        imageName: Pumpkin
    },{
        id: 1006,
        name: "Ladies' Fingers",
        price: 400,
        bio: "Grown in Nuwara Eliya, Sri Lanka",
        imageName: ladiesFingers
    }];

    function convertBase64(pic) {
        console.log(Carrot);
    }



    const [pageNum, setPageNum] = useState(1);
    const [isEdit, setIsEdit] = useState(false);

    function pageSelect() {
        if (pageNum == 1) {
            return <ShopComponent itemList={itemList}/>
        }else if (pageNum == 2) {
            return <MangeItem itemList={itemList} setIsEdit={(data) => setIsEdit(data)} isEdit={isEdit}/>
        }else if (pageNum == 3) {
            return <Cart cart={cart} setPageNum={(data) => setPageNum(data)}/>
        }
    }

    function pageTitle() {
        if (pageNum == 1) {
            return "Shop"
        }else if (pageNum == 2) {
            return "Manage Item"
        }else if (pageNum == 3) {
            return "Cart"
        }
    }

    return(
        <div className="shop-container">
            <Nav setPageNum={(data) => setPageNum(data)} cart={cart}/>
            <Title pageTitle={pageTitle}/>
            {
                pageSelect()
            }
            
        </div>
    )
}

export default Shop;
