import React, {useEffect, useState} from "react";

function ManageForm({setBase64, base64, addItem}) {

    const base64Convert = (e) => {
        let reader = new FileReader();
        reader.readAsDataURL(e.target.files[0]);
        reader.onload = () => {
            setBase64(reader.result);
        };
    };

    return(
        <div className="form-box">
            <div className="from-name">Add New Item</div>
            <div className="form-details">
                <div className="form-details-conatiner">
                    <div>ID: <input className='manage-item-name id-input' type="number" placeholder="Enter ID"/></div>
                    <div>Name: <input className='manage-item-name name-input' type="text" placeholder="Enter Name"/></div>
                    <div>Price /1Kg:  <input className='manage-price price-input' type="number" placeholder="Enter Price"/></div>
                </div>
                <div>
                    <img className='mange-image' src={base64}/>
                    <input onChange={(e) => {base64Convert(e)}} accept="image/*" type="file" />
                </div>
            </div>
            <div className="bio-details">
                <div>Bio: <input className='manage-bio bio-input' type="text" placeholder="Enter the bio"/></div>
                <button className='save-button' onClick={() => addItem({id,name,price,imageName: base64})}>Add</button>
            </div>
        </div>
    );
}

export default ManageForm;
