import Nav from "./Nav.js";
import Title from "./Title.js";
import ShopComponent from "./ShopComponent.js";
import Carrot from './carrots.jpg';
import Beans from './green_beans.jpeg';
import Carbbage from './carbbage.jpg';
import ladiesFingers from './ladies_finger.jpg';
import Pumpkin from './Pumpkin.webp';
import { useState } from "react";

const itemList = [{
    id: 1005,
    name: "Carrot",
    price: 165,
    bio: "Grown in Nuwara Eliya, Sri Lanka",
    imageName: Carrot
},{
    id: 1007,
    name: "Beans",
    price: 758,
    bio: "Grown in Matale, Sri Lanka",
    imageName: Beans
},{
    id: 1009,
    name: "Cabbage",
    price: 105,
    bio: "Grown in Nuwara Eliya, Sri Lanka",
    imageName: Carbbage
},{
    id: 1004,
    name: "Pumpkin",
    price: 400,
    bio: "Grown in Yala, Sri Lanka",
    imageName: Pumpkin
},{
    id: 1006,
    name: "Ladies' Fingers",
    price: 400,
    bio: "Grown in Nuwara Eliya, Sri Lanka",
    imageName: ladiesFingers
}];

function Shop() {

    const [pageNum, setPageNum] = useState(1);

    function pageSelect() {
        if (pageNum == 1) {
            <ShopComponent itemList={itemList}/>
        }
    }

    return(
        <div className="shop-container">
            <Nav/>
            <Title/>
            {
                pageSelect()
            }
            
        </div>
    )
}

export default Shop;
