import ManageForm from "./ManageForm.js";

function MangeItem( { itemList, setIsEdit, isEdit } ) {
    return(
        <div className="manage-container">
            <ManageForm/>
            {
                itemList.map( (item,index) => {
                    return(
                        <div key={index} className="manage-item">
                            <img src={item.imageName} className='mange-image' />
                            <div className='manage-item-details'>
                                <div>
                                    <input className='manage-item-name' value={item.name} type='text'/>
                                    <input className='manage-price' value={item.price} type='text'/>/1Kg
                                </div>
                                <div>
                                    <input className='manage-bio' value={item.bio} type='text'/>
                                </div>
                            </div>
                            <div className='manage-button-list'>
                                <button className='save-button' onClick={() => {chnageSaveToEdit()}}>
                                    Edit
                                </button>
                                <button className='delete-button'>
                                    Delete
                                </button>
                            </div>
                        </div> 
                    )
                })
            }
        </div>
    )
}

export default MangeItem;
